NSString * const articleEn = @"China has published a report on the human rights situation in the US, which exposed Washington's human rights violations in such areas as the prevalence of money politics, rising income inequality and worsening racial discrimination. The report, titled \"Human Rights Record of the United States in 2018\" was released by the Information Office of the State Council on Thursday. \n It said the US government, a self-styled \"human rights defender\", has a human rights record which is flawed and lackluster, and the double standards of human rights it pursues are obvious.According to the report, the US 2018 midterm elections cost a huge quantity of money as elections in the country became games of money, with much involvement of \"dark money\" and corruption.";

@interface VVMock ()
@property (nonatomic, strong) NSArray *charArray;
@property (nonatomic, strong) NSArray *firstNameArray;
@property (nonatomic, strong) NSArray *lastNameArray;
@end

@implementation VVMock

+ (instancetype)shared{
    static VVMock *_mock;
    static dispatch_once_t onceToken;
    dispatch_once(&onceToken, ^{
        _mock = [[self alloc] init];
    });
    return _mock;
}

- (instancetype)init{
    self = [super init];
    if (self) {
        _charArray = [chars componentsSeparatedByString:@" "];
        _firstNameArray = [firstNames componentsSeparatedByString:@" "];
        _lastNameArray = [lastNames componentsSeparatedByString:@" "];
    }
    return self;
}

- (NSString *)nick{
    return [self charTextWithMinLen:4 maxLen:16];
}

- (NSString *)name{
    NSString *first = _firstNameArray[arc4random_uniform((uint32_t)_firstNameArray.count)];
    NSString *last = _firstNameArray[arc4random_uniform((uint32_t)_lastNameArray.count)];
    return [NSString stringWithFormat:@"%@%@",last,first];
}

- (NSString *)shortText{
    NSInteger loc = arc4random_uniform(3885 - 20);
    NSInteger len = 2 + arc4random_uniform(18);
    return [article substringWithRange:NSMakeRange(loc, len)];
}

- (NSString *)longText{
    NSInteger loc = arc4random_uniform(3885 - 100);
    NSInteger len = 20 + arc4random_uniform(80);
    return [article substringWithRange:NSMakeRange(loc, len)];
}

- (NSString *)longLongText{
    NSInteger loc = arc4random_uniform(3885 - 1000);
    NSInteger len = 100 + arc4random_uniform(900);
    return [article substringWithRange:NSMakeRange(loc, len)];
}

- (NSString *)textEn{
    NSInteger loc = arc4random_uniform(757 - 100);
    NSInteger len = 20 + arc4random_uniform(80);
    return [articleEn substringWithRange:NSMakeRange(loc, len)];
}

- (NSString *)mobile{
    return [NSString stringWithFormat:@"+86%@%@%@",
            @(130 + arc4random_uniform(60)),
            @(1000 +arc4random_uniform(8999)),
            @(1000 +arc4random_uniform(8999))];
}

- (NSString *)email{
    return [NSString stringWithFormat:@"%@@%@.com",[self charTextWithMinLen:4 maxLen:16],[self charTextWithMinLen:3 maxLen:8]];
}

//MARK: - Private
- (NSString *)charTextWithMinLen:(NSUInteger)minLen maxLen:(NSUInteger)maxLen{
    NSInteger len = minLen + arc4random_uniform((uint32_t)(maxLen - minLen));
    NSMutableString *text = [NSMutableString stringWithCapacity:0];
    uint32_t count = (uint32_t)_charArray.count;
    for (NSInteger i = 0; i < len; i ++) {
        NSInteger idx = arc4random_uniform(count);
        [text appendString:_charArray[idx]];
    }
    return text;
}


@end
